from random import randint, choices

def merge_the_tools(string, k):
    str_len = len(string)
    sub_str_len = k
    for sub_str_start in range(0, str_len, sub_str_len):
        sub_str = string[sub_str_start:sub_str_start+sub_str_len]
        sub_str_no_repeats = ''.join(dict.fromkeys(list(sub_str)).keys())
        print(sub_str_no_repeats)

